import { TemplateRef } from '@angular/core';
import { PassThroughOption, PassThrough } from 'primeng/api';

/**
 * Custom pass-through(pt) options.
 * @template I Type of instance.
 *
 * @see {@link Tag.pt}
 * @group Interface
 */
interface TagPassThroughOptions<I = unknown> {
    /**
     * Used to pass attributes to the host's DOM element.
     */
    host?: PassThroughOption<HTMLElement, I>;
    /**
     * Used to pass attributes to the root's DOM element.
     */
    root?: PassThroughOption<HTMLSpanElement, I>;
    /**
     * Used to pass attributes to the icon's DOM element.
     */
    icon?: PassThroughOption<HTMLSpanElement, I>;
    /**
     * Used to pass attributes to the label's DOM element.
     */
    label?: PassThroughOption<HTMLSpanElement, I>;
}
/**
 * Defines valid pass-through options in Tag.
 * @see {@link TagPassThroughOptions}
 *
 * @template I Type of instance.
 */
type TagPassThrough<I = unknown> = PassThrough<I, TagPassThroughOptions<I>>;
/**
 * Defines valid templates in Tag.
 * @group Templates
 */
interface TagTemplates {
    /**
     * Custom icon template.
     */
    icon(): TemplateRef<void>;
}

export type { TagPassThrough, TagPassThroughOptions, TagTemplates };
